const modal=new openModal,countClicks=(modal.showModal(),new clickCounter);countClicks.count();class clickCounter{constructor(){this.button=document.querySelector(".button"),this.span=document.querySelector(".modal__clicks"),this.number=sessionStorage.getItem("numbers"),this.resetButton=document.querySelector(".reset-button")}count(){this.button.addEventListener("click",()=>{this.number++,this.span.textContent=this.number+" times",this.showResetButton(),sessionStorage.setItem("numbers",this.number)}),this.resetNumber(),this.saveNumber()}showResetButton(){2<this.number&&this.resetButton.classList.add("reset-button--active")}resetNumber(){this.resetButton.addEventListener("click",()=>{this.number=0,this.span.textContent=this.number+" times",this.resetButton.classList.remove("reset-button--active"),sessionStorage.clear()})}saveNumber(){sessionStorage.getItem("numbers")}}class openModal{constructor(){this.button=document.querySelector(".button"),this.closeButton=document.querySelector(".close-button"),this.modal=document.querySelector(".modal")}toggleModal(){this.modal.classList.toggle("modal--active")}open(){this.button.addEventListener("click",()=>{this.toggleModal()})}close(){this.closeButton.addEventListener("click",()=>{this.toggleModal()})}closingByClickingOnDocument(){document.addEventListener("click",()=>{this.modal.classList.contains("modal--active")&&(this.modal.classList.remove("modal--active"),document.querySelector(".modal__window").style.pointerEvents="none")})}showModal(){this.open(),this.close()}}